// Generated by Copilot
// GitHub API Client - Direct GitHub API integration for bookmarklet use
// Self-contained client with no Chrome extension API dependencies

export interface IGitHubAPIConfig {
  token: string;
  repository: string;
  branch: string;
  baseUrl?: string;
}

export interface IGitHubCommitResult {
  success: boolean;
  data?: {
    sha: string;
    html_url: string;
    commit: {
      message: string;
      author: {
        name: string;
        email: string;
        date: string;
      };
    };
  };
  error?: string;
}

export interface IGitHubFileContent {
  type: string;
  encoding: string;
  size: number;
  name: string;
  path: string;
  content: string;
  sha: string;
  url: string;
  git_url: string;
  html_url: string;
  download_url: string;
}

/**
 * Direct GitHub API client for bookmarklet use
 * Handles file creation, updates, and repository operations
 */
export class GitHubAPIClient {
  private _config: IGitHubAPIConfig;
  private _baseUrl: string;

  constructor(config: IGitHubAPIConfig) {
    this._config = {
      baseUrl: 'https://api.github.com',
      ...config,
    };
    this._baseUrl = this._config.baseUrl!;
  }

  /**
   * Commit a file to the repository
   */
  async commitFile(path: string, content: string, message: string): Promise<IGitHubCommitResult> {
    try {
      const [owner, repo] = this.parseRepository(this._config.repository);

      // Check if file already exists
      const existingFile = await this.getFile(path);

      // Prepare commit data
      const commitData: any = {
        message,
        content: this.encodeBase64(content),
        branch: this._config.branch,
      };

      // Include SHA if file exists (for updates)
      if (existingFile) {
        commitData.sha = existingFile.sha;
      }

      // Make API request
      const response = await this.makeRequest(
        `repos/${owner}/${repo}/contents/${path}`,
        'PUT',
        commitData
      );

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(
          `GitHub API error: ${response.status} - ${errorData.message || 'Unknown error'}`
        );
      }

      const result = await response.json();

      return {
        success: true,
        data: {
          sha: result.commit.sha,
          html_url: result.content.html_url,
          commit: {
            message: result.commit.message,
            author: {
              name: result.commit.author.name,
              email: result.commit.author.email,
              date: result.commit.author.date,
            },
          },
        },
      };
    } catch (error) {
      console.error('GitHub commit failed:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
      };
    }
  }

  /**
   * Get file content from repository
   */
  async getFile(path: string): Promise<IGitHubFileContent | null> {
    try {
      const [owner, repo] = this.parseRepository(this._config.repository);

      const response = await this.makeRequest(
        `repos/${owner}/${repo}/contents/${path}?ref=${this._config.branch}`,
        'GET'
      );

      if (response.status === 404) {
        return null; // File doesn't exist
      }

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(
          `GitHub API error: ${response.status} - ${errorData.message || 'Unknown error'}`
        );
      }

      return await response.json();
    } catch (error) {
      console.error('Failed to get file from GitHub:', error);
      return null;
    }
  }

  /**
   * Test repository access and permissions
   */
  async testConnection(): Promise<{
    success: boolean;
    user?: {
      login: string;
      name: string;
      email: string;
    };
    repository?: {
      name: string;
      full_name: string;
      private: boolean;
      permissions: {
        admin: boolean;
        push: boolean;
        pull: boolean;
      };
    };
    error?: string;
  }> {
    try {
      // Test 1: Validate GitHub token by getting user info
      const userResponse = await this.makeRequest('user', 'GET');

      if (!userResponse.ok) {
        const errorData = await userResponse.json().catch(() => ({}));
        return {
          success: false,
          error: `Invalid GitHub token (${userResponse.status}): ${errorData.message || 'Unknown error'}`,
        };
      }

      const userData = await userResponse.json();

      // Test 2: Check repository access
      const [owner, repo] = this.parseRepository(this._config.repository);
      const repoResponse = await this.makeRequest(`repos/${owner}/${repo}`, 'GET');

      if (!repoResponse.ok) {
        const errorMsg =
          repoResponse.status === 404
            ? 'Repository not found or no access'
            : `Repository access failed (${repoResponse.status})`;
        return {
          success: false,
          error: errorMsg,
        };
      }

      const repoData = await repoResponse.json();

      return {
        success: true,
        user: {
          login: userData.login,
          name: userData.name || userData.login,
          email: userData.email || '',
        },
        repository: {
          name: repoData.name,
          full_name: repoData.full_name,
          private: repoData.private,
          permissions: repoData.permissions || { admin: false, push: false, pull: true },
        },
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Connection test failed',
      };
    }
  }

  /**
   * List files in a directory
   */
  async listFiles(path: string = ''): Promise<{
    success: boolean;
    files?: Array<{
      name: string;
      path: string;
      type: 'file' | 'dir';
      size: number;
      sha: string;
      url: string;
    }>;
    error?: string;
  }> {
    try {
      const [owner, repo] = this.parseRepository(this._config.repository);

      const response = await this.makeRequest(
        `repos/${owner}/${repo}/contents/${path}?ref=${this._config.branch}`,
        'GET'
      );

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        return {
          success: false,
          error: `Failed to list files: ${response.status} - ${errorData.message || 'Unknown error'}`,
        };
      }

      const data = await response.json();
      const files = Array.isArray(data) ? data : [data];

      return {
        success: true,
        files: files.map(file => ({
          name: file.name,
          path: file.path,
          type: file.type === 'dir' ? 'dir' : 'file',
          size: file.size || 0,
          sha: file.sha,
          url: file.html_url,
        })),
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Failed to list files',
      };
    }
  }

  /**
   * Create a directory by committing a placeholder file
   */
  async createDirectory(path: string): Promise<IGitHubCommitResult> {
    const placeholderPath = `${path}/.gitkeep`;
    const placeholderContent = '# This file maintains the directory structure\n';
    const message = `Create directory: ${path}`;

    return await this.commitFile(placeholderPath, placeholderContent, message);
  }

  /**
   * Get repository statistics
   */
  async getRepositoryStats(): Promise<{
    success: boolean;
    stats?: {
      size: number;
      stargazers_count: number;
      watchers_count: number;
      forks_count: number;
      open_issues_count: number;
      default_branch: string;
      updated_at: string;
    };
    error?: string;
  }> {
    try {
      const [owner, repo] = this.parseRepository(this._config.repository);

      const response = await this.makeRequest(`repos/${owner}/${repo}`, 'GET');

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        return {
          success: false,
          error: `Failed to get repository stats: ${response.status} - ${errorData.message || 'Unknown error'}`,
        };
      }

      const data = await response.json();

      return {
        success: true,
        stats: {
          size: data.size,
          stargazers_count: data.stargazers_count,
          watchers_count: data.watchers_count,
          forks_count: data.forks_count,
          open_issues_count: data.open_issues_count,
          default_branch: data.default_branch,
          updated_at: data.updated_at,
        },
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Failed to get repository stats',
      };
    }
  }

  // Private helper methods

  private async makeRequest(
    endpoint: string,
    method: 'GET' | 'POST' | 'PUT' | 'DELETE' = 'GET',
    body?: any
  ): Promise<Response> {
    const url = `${this._baseUrl}/${endpoint}`;

    const headers: HeadersInit = {
      Authorization: `token ${this._config.token}`,
      Accept: 'application/vnd.github.v3+json',
      'User-Agent': 'PrismWeave-Bookmarklet/1.0',
    };

    if (body && (method === 'POST' || method === 'PUT')) {
      headers['Content-Type'] = 'application/json';
    }

    const requestOptions: RequestInit = {
      method,
      headers,
      ...(body && { body: JSON.stringify(body) }),
    };

    return await fetch(url, requestOptions);
  }

  private parseRepository(repository: string): [string, string] {
    const parts = repository.split('/');
    if (parts.length !== 2) {
      throw new Error(`Invalid repository format: ${repository}. Expected "owner/repo"`);
    }
    return [parts[0], parts[1]];
  }

  private encodeBase64(content: string): string {
    try {
      // Use btoa for browser environments
      return btoa(unescape(encodeURIComponent(content)));
    } catch (error) {
      // Fallback for environments without btoa
      const bytes = new TextEncoder().encode(content);
      let binary = '';
      bytes.forEach(byte => {
        binary += String.fromCharCode(byte);
      });
      return btoa(binary);
    }
  }

  private decodeBase64(encoded: string): string {
    try {
      return decodeURIComponent(escape(atob(encoded)));
    } catch (error) {
      // Fallback
      const binary = atob(encoded);
      const bytes = new Uint8Array(binary.length);
      for (let i = 0; i < binary.length; i++) {
        bytes[i] = binary.charCodeAt(i);
      }
      return new TextDecoder().decode(bytes);
    }
  }

  /**
   * Update configuration
   */
  updateConfig(updates: Partial<IGitHubAPIConfig>): void {
    this._config = { ...this._config, ...updates };
    if (updates.baseUrl) {
      this._baseUrl = updates.baseUrl;
    }
  }

  /**
   * Get current configuration (without sensitive data)
   */
  getConfig(): Omit<IGitHubAPIConfig, 'token'> {
    const { token, ...config } = this._config;
    return config;
  }
}
