#!/bin/bash
# Generated by Copilot
# Setup script for PrismWeave CLI tests

echo "========================================"
echo "  PrismWeave CLI Test Setup"
echo "========================================"
echo ""

# Check if Node.js is installed
if ! command -v node &> /dev/null; then
    echo "âŒ Node.js is not installed. Please install Node.js 20.x or higher."
    exit 1
fi

# Check Node.js version
NODE_VERSION=$(node -v | cut -d'v' -f2 | cut -d'.' -f1)
if [ "$NODE_VERSION" -lt 20 ]; then
    echo "âš ï¸  Node.js version 20.x or higher is recommended. Current version: $(node -v)"
fi

echo "âœ“ Node.js version: $(node -v)"
echo ""

# Install dependencies
echo "ğŸ“¦ Installing dependencies..."
npm install

if [ $? -ne 0 ]; then
    echo "âŒ Failed to install dependencies"
    exit 1
fi

echo "âœ“ Dependencies installed"
echo ""

# Build TypeScript
echo "ğŸ”¨ Building TypeScript..."
npm run build

if [ $? -ne 0 ]; then
    echo "âŒ Build failed"
    exit 1
fi

echo "âœ“ Build successful"
echo ""

# Run tests
echo "ğŸ§ª Running tests..."
npm test

if [ $? -ne 0 ]; then
    echo "âŒ Some tests failed"
    exit 1
fi

echo ""
echo "âœ“ All tests passed!"
echo ""

# Generate coverage report
echo "ğŸ“Š Generating coverage report..."
npm run test:coverage

echo ""
echo "========================================"
echo "  Setup Complete!"
echo "========================================"
echo ""
echo "Coverage report available at:"
